import { useState, useEffect, useRef } from 'react';
import { BarChart, Bar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar, ResponsiveContainer, Cell } from 'recharts';
import './App.css';

// Custom Hook for count-up animation
function useCountUp(end, duration = 1500, shouldStart = true) {
  const [count, setCount] = useState(0);
  
  useEffect(() => {
    if (!shouldStart) return;
    
    let start = 0;
    const increment = end / (duration / 16);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        setCount(end);
        clearInterval(timer);
      } else {
        setCount(Math.floor(start));
      }
    }, 16);
    
    return () => clearInterval(timer);
  }, [end, duration, shouldStart]);
  
  return count;
}

// Header Component
function Header() {
  const [isDark, setIsDark] = useState(false);
  
  const toggleTheme = () => {
    setIsDark(!isDark);
    document.documentElement.setAttribute('data-theme', !isDark ? 'dark' : 'light');
  };
  
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            CREDExA
            <span className="version">v1.2.0</span>
          </div>
          <div style={{ display: 'flex', gap: 'var(--space-4)', alignItems: 'center' }}>
            <button className="theme-toggle" onClick={toggleTheme} title="Toggle theme">
              {isDark ? 'â˜€ï¸' : 'ðŸŒ™'}
            </button>
            <div className="trust-badge">
              <span>ðŸ”’</span>
              <span>Built for transparency</span>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}

// Hero Component
function Hero({ onAnalyzeClick, onSampleDemo }) {
  const statsCount1 = useCountUp(1247, 2000, true);
  const statsCount2 = useCountUp(12, 2000, true);
  
  return (
    <section className="hero">
      <div className="hero-particles">
        {[...Array(20)].map((_, i) => (
          <div key={i} className="particle" style={{
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
            animationDuration: `${5 + Math.random() * 10}s`
          }} />
        ))}
      </div>
      <div className="container">
        <div className="hero-content">
          <div className="hero-eyebrow">Resume Analysis System</div>
          <h1 className="hero-headline">
            Understand why candidates matchâ€”or don't
          </h1>
          <p className="hero-subhead">
            Explainable scoring for better hiring decisions
          </p>
          <div className="hero-cta">
            <button className="btn btn-primary btn-hover-lift" onClick={onAnalyzeClick}>
              Analyze Resume â†’
            </button>
            <button className="btn btn-secondary btn-hover-lift" onClick={onSampleDemo}>
              âš¡ Try Sample Demo
            </button>
          </div>
          <div className="hero-stats">
            <div className="stat">
              <span className="stat-number">{statsCount1.toLocaleString()}</span>
              <span className="stat-label">Analyses completed</span>
            </div>
            <div className="stat">
              <span className="stat-number">{statsCount2} min</span>
              <span className="stat-label">Avg. time saved</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

// Analyze Section Component
function AnalyzeSection({ onAnalyze, analyzeRef, resumeFile, setResumeFile, jobDescription, setJobDescription }) {
  const fileInputRef = useRef(null);
  
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setResumeFile(file);
    }
  };
  
  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file && (file.type === 'application/pdf' || file.name.endsWith('.pdf'))) {
      setResumeFile(file);
    }
  };
  
  const handleAnalyze = () => {
    if (resumeFile && jobDescription.trim()) {
      onAnalyze();
    }
  };
  
  return (
    <section className="analyze section" ref={analyzeRef}>
      <div className="container">
        <div className="text-center">
          <h2 className="section-title">Analyze Resume</h2>
          <p className="section-subtitle">
            Upload a resume and job description to see explainable match scoring
          </p>
        </div>
        
        <div 
          className={`upload-area ${resumeFile ? 'has-file' : ''}`}
          onClick={() => fileInputRef.current?.click()}
          onDrop={handleDrop}
          onDragOver={(e) => e.preventDefault()}
        >
          <div className="upload-icon">{resumeFile ? 'âœ“' : 'ðŸ“„'}</div>
          <div className="upload-title">
            {resumeFile ? resumeFile.name : 'Upload Resume'}
          </div>
          <p className="upload-subtitle">
            {resumeFile ? 'Click to change file' : 'Click to browse or drag and drop PDF files'}
          </p>
          <input 
            ref={fileInputRef}
            type="file" 
            accept=".pdf,.docx"
            className="upload-input"
            onChange={handleFileChange}
          />
        </div>
        
        <div className="upload-divider">or</div>
        
        <div className="job-description-area">
          <label htmlFor="job-desc" className="section-title" style={{ fontSize: 'var(--text-xl)', display: 'block', marginBottom: 'var(--space-3)' }}>
            Job Description
          </label>
          <textarea
            id="job-desc"
            className="textarea"
            placeholder="Paste the job description here..."
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
          />
          <div className="char-count">{jobDescription.length} characters</div>
        </div>
        
        <div className="analyze-btn-container">
          <button 
            className="btn btn-primary btn-hover-lift btn-pulse"
            onClick={handleAnalyze}
            disabled={!resumeFile || !jobDescription.trim()}
            style={{ opacity: (!resumeFile || !jobDescription.trim()) ? 0.5 : 1 }}
          >
            ðŸš€ Analyze Match
          </button>
        </div>
      </div>
    </section>
  );
}

// Loading Component with Real-time Feedback
function LoadingState() {
  const [activeStep, setActiveStep] = useState(0);
  const [progress, setProgress] = useState(0);
  
  useEffect(() => {
    const stepTimer = setInterval(() => {
      setActiveStep(prev => (prev < 2 ? prev + 1 : prev));
    }, 1000);
    
    const progressTimer = setInterval(() => {
      setProgress(prev => (prev < 100 ? prev + 2 : 100));
    }, 60);
    
    return () => {
      clearInterval(stepTimer);
      clearInterval(progressTimer);
    };
  }, []);
  
  return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <div className="loading-text">Analyzing resume...</div>
      <div className="progress-bar-container">
        <div className="progress-bar-fill" style={{ width: `${progress}%` }}></div>
      </div>
      <div className="progress-percentage">{progress}%</div>
      <div className="loading-steps">
        <div className={`loading-step ${activeStep >= 0 ? 'completed' : ''}`}>
          <span className="step-icon">{activeStep >= 0 ? 'âœ“' : 'â—‹'}</span>
          <span>Parsing document</span>
        </div>
        <div className={`loading-step ${activeStep === 1 ? 'active' : activeStep > 1 ? 'completed' : ''}`}>
          <span className="step-icon">{activeStep > 1 ? 'âœ“' : activeStep === 1 ? 'âŸ³' : 'â—‹'}</span>
          <span>Extracting skills</span>
        </div>
        <div className={`loading-step ${activeStep === 2 ? 'active' : ''}`}>
          <span className="step-icon">{activeStep === 2 ? 'âŸ³' : 'â—‹'}</span>
          <span>Computing match</span>
        </div>
      </div>
    </div>
  );
}

// Skill Pills Component
function SkillPills({ skills }) {
  return (
    <div className="skills-container">
      <h4 className="skills-title">Skills Analysis</h4>
      <div className="skills-grid">
        {skills.map((skill, index) => (
          <div 
            key={index} 
            className={`skill-pill ${skill.matched ? 'matched' : 'missing'}`}
            style={{ animationDelay: `${index * 0.05}s` }}
          >
            <span className="skill-icon">{skill.matched ? 'âœ“' : 'âœ—'}</span>
            <span className="skill-name">{skill.name}</span>
            {skill.matched && <span className="skill-level">{skill.level}%</span>}
          </div>
        ))}
      </div>
    </div>
  );
}

// Results Component with Data Visualization
function ResultsSection({ resultsRef }) {
  const [viewMode, setViewMode] = useState('detailed');
  const [showResults, setShowResults] = useState(true);
  const [showConfetti, setShowConfetti] = useState(false);
  
  const scoreValue = 87;
  const scoreCount = useCountUp(scoreValue, 1500, showResults);
  
  useEffect(() => {
    if (scoreValue >= 85) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 3000);
    }
  }, [scoreValue]);
  
  const getScoreColor = (score) => {
    if (score >= 90) return 'var(--green-500)';
    if (score >= 75) return 'var(--blue-600)';
    if (score >= 60) return 'var(--amber-500)';
    return 'var(--red-500)';
  };
  
  const getScoreBadge = (score) => {
    if (score >= 90) return { text: 'Excellent Fit', class: 'badge-strong' };
    if (score >= 75) return { text: 'Strong Fit', class: 'badge-strong' };
    if (score >= 60) return { text: 'Moderate Fit', class: 'badge-moderate' };
    return { text: 'Weak Fit', class: 'badge-weak' };
  };
  
  const keyFactors = [
    { label: 'Skills Match', score: 92, color: 'var(--green-500)' },
    { label: 'Experience', score: 85, color: 'var(--blue-600)' },
    { label: 'Education', score: 81, color: 'var(--blue-600)' },
    { label: 'Certifications', score: 78, color: 'var(--amber-500)' }
  ];
  
  const radarData = [
    { subject: 'Technical Skills', score: 92, fullMark: 100 },
    { subject: 'Experience', score: 85, fullMark: 100 },
    { subject: 'Education', score: 81, fullMark: 100 },
    { subject: 'Communication', score: 75, fullMark: 100 },
    { subject: 'Leadership', score: 68, fullMark: 100 }
  ];
  
  const barData = keyFactors.map(f => ({ name: f.label, value: f.score }));
  
  const skills = [
    { name: 'Python', matched: true, level: 95 },
    { name: 'React', matched: true, level: 90 },
    { name: 'Node.js', matched: true, level: 85 },
    { name: 'TypeScript', matched: true, level: 88 },
    { name: 'Docker', matched: false },
    { name: 'GraphQL', matched: false },
    { name: 'AWS', matched: true, level: 78 },
    { name: 'MongoDB', matched: true, level: 82 }
  ];
  
  const explainabilityItems = [
    { type: 'positive', icon: 'âœ“', text: '8/10 required skills present', points: '+12 pts' },
    { type: 'positive', icon: 'âœ“', text: '4 years experience (3+ needed)', points: '+8 pts' },
    { type: 'positive', icon: 'âœ“', text: 'Relevant degree in Computer Science', points: '+6 pts' },
    { type: 'warning', icon: 'âš ', text: 'Missing: Docker, GraphQL', points: '-7 pts' },
    { type: 'warning', icon: 'âš ', text: 'Limited leadership experience', points: '-3 pts' }
  ];
  
  const suggestions = [
    { priority: 'high', action: 'Add Docker to technical skills section', impact: '+5-7 points' },
    { priority: 'high', action: 'Highlight GraphQL in project descriptions', impact: '+3-5 points' },
    { priority: 'medium', action: 'Quantify leadership experience with metrics', impact: '+2-4 points' },
    { priority: 'low', action: 'Add relevant certifications (AWS, Azure)', impact: '+1-3 points' }
  ];
  
  const badge = getScoreBadge(scoreValue);
  
  const handleExport = () => {
    // Create a simple text report
    const report = `
CREDExA Analysis Report
=======================
Match Score: ${scoreValue}%
Status: ${badge.text}

Key Factors:
${keyFactors.map(f => `- ${f.label}: ${f.score}%`).join('\n')}

Skills Matched: ${skills.filter(s => s.matched).length}/${skills.length}

Suggestions for Improvement:
${suggestions.map((s, i) => `${i + 1}. ${s.action} (${s.impact})`).join('\n')}

Generated by CREDExA v1.2.0
    `;
    
    const blob = new Blob([report], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `credexa-report-${Date.now()}.txt`;
    a.click();
    URL.revokeObjectURL(url);
  };
  
  return (
    <section className="results section" ref={resultsRef}>
      {showConfetti && <div className="confetti-container">
        {[...Array(50)].map((_, i) => (
          <div key={i} className="confetti" style={{
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 0.5}s`,
            background: ['#3b82f6', '#10b981', '#f59e0b', '#ef4444'][Math.floor(Math.random() * 4)]
          }} />
        ))}
      </div>}
      
      <div className="container">
        <div className="text-center">
          <h2 className="section-title">Analysis Results</h2>
          <p className="section-subtitle">
            Detailed breakdown of resume-job match
          </p>
        </div>
        
        <div className="results-actions">
          <div className="view-toggle">
            <button 
              className={`toggle-btn ${viewMode === 'simple' ? 'active' : ''}`}
              onClick={() => setViewMode('simple')}
            >
              Simple View
            </button>
            <button 
              className={`toggle-btn ${viewMode === 'detailed' ? 'active' : ''}`}
              onClick={() => setViewMode('detailed')}
            >
              Detailed View
            </button>
          </div>
          
          <button className="btn btn-secondary btn-export" onClick={handleExport}>
            ðŸ“¥ Export Report
          </button>
        </div>
        
        <div className="results-grid">
          <div className="card card-elevated card-interactive">
            <div className="score-display">
              <div 
                className="score-number score-pulse" 
                style={{ color: getScoreColor(scoreValue) }}
              >
                {scoreCount}%
              </div>
              <div className="score-label">Match Score</div>
              <div className="score-bar">
                <div 
                  className="score-bar-fill"
                  style={{ 
                    '--score-percentage': `${scoreValue}%`,
                    background: getScoreColor(scoreValue)
                  }}
                ></div>
              </div>
              <span className={`score-badge ${badge.class}`}>
                {badge.text}
              </span>
              <div className="confidence-meter">
                <span className="confidence-label">Confidence</span>
                <div className="confidence-bar">
                  <div className="confidence-fill" style={{ '--score-percentage': '85%' }}></div>
                </div>
                <span className="confidence-value">85%</span>
              </div>
            </div>
          </div>
          
          <div className="card card-elevated card-interactive">
            <div className="card-header">
              <h3 className="card-title">Key Factors</h3>
              <p className="card-subtitle">Primary scoring components</p>
            </div>
            <div className="factors-list">
              {keyFactors.map((factor, index) => (
                <div key={index} className="factor factor-interactive">
                  <span className="factor-label">{factor.label}</span>
                  <div className="factor-bar-container">
                    <div className="factor-bar">
                      <div 
                        className="factor-bar-fill" 
                        style={{ 
                          '--score-percentage': `${factor.score}%`,
                          background: factor.color,
                          animationDelay: `${index * 0.1}s`
                        }}
                      ></div>
                    </div>
                    <span className="factor-score" style={{ color: factor.color }}>
                      {factor.score}%
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        
        {viewMode === 'detailed' && (
          <>
            <SkillPills skills={skills} />
            
            <div className="visualization-grid">
              <div className="card card-interactive">
                <div className="card-header">
                  <h3 className="card-title">Skills Radar</h3>
                  <p className="card-subtitle">Multi-dimensional analysis</p>
                </div>
                <ResponsiveContainer width="100%" height={300}>
                  <RadarChart data={radarData}>
                    <PolarGrid stroke="var(--gray-300)" />
                    <PolarAngleAxis dataKey="subject" tick={{ fill: 'var(--gray-600)', fontSize: 12 }} />
                    <PolarRadiusAxis angle={90} domain={[0, 100]} tick={{ fill: 'var(--gray-500)' }} />
                    <Radar name="Score" dataKey="score" stroke="var(--blue-600)" fill="var(--blue-600)" fillOpacity={0.6} />
                  </RadarChart>
                </ResponsiveContainer>
              </div>
              
              <div className="card card-interactive">
                <div className="card-header">
                  <h3 className="card-title">Factor Distribution</h3>
                  <p className="card-subtitle">Comparative breakdown</p>
                </div>
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={barData}>
                    <Bar dataKey="value" radius={[8, 8, 0, 0]}>
                      {barData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={keyFactors[index].color} />
                      ))}
                    </Bar>
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>
            
            <div className="card card-glass">
              <div className="card-header">
                <h3 className="card-title">Why This Score?</h3>
                <p className="card-subtitle">Transparent scoring breakdown</p>
              </div>
              <div className="explainability-panel">
                <div className="explainability-list">
                  {explainabilityItems.map((item, index) => (
                    <div key={index} className={`explainability-item ${item.type}`}>
                      <span className="explainability-icon">{item.icon}</span>
                      <div className="explainability-content">
                        <div className="explainability-text">{item.text}</div>
                        <div className="explainability-points">{item.points}</div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
            
            <div className="card suggestions-panel card-interactive">
              <div className="card-header">
                <h3 className="card-title">ðŸ’¡ Improvement Suggestions</h3>
                <p className="card-subtitle">Actionable recommendations</p>
              </div>
              <div className="suggestions-list">
                {suggestions.map((suggestion, index) => (
                  <div key={index} className="suggestion suggestion-hover">
                    <span className={`priority-dot priority-${suggestion.priority}`}></span>
                    <div className="suggestion-content">
                      <div className="suggestion-action">{suggestion.action}</div>
                      <div className="suggestion-impact">Potential impact: {suggestion.impact}</div>
                    </div>
                    <button className="suggestion-apply">Apply</button>
                  </div>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    </section>
  );
}

