// Main App Component
function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState('');
  const analyzeRef = useRef(null);
  const resultsRef = useRef(null);
  
  const scrollToAnalyze = () => {
    analyzeRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
  
  const handleSampleDemo = () => {
    setResumeFile({ name: 'sample_resume_john_doe.pdf' });
    setJobDescription(`Senior Full Stack Developer

Required Skills:
- Python, React, Node.js, TypeScript
- Docker, Kubernetes, GraphQL
- AWS Cloud Services
- 3+ years experience
- Bachelor's degree in Computer Science

Responsibilities:
- Build scalable web applications
- Lead technical projects
- Mentor junior developers`);
    
    setTimeout(() => {
      analyzeRef.current?.scrollIntoView({ behavior: 'smooth' });
      setTimeout(() => handleAnalyze(), 500);
    }, 100);
  };
  
  const handleAnalyze = () => {
    setIsLoading(true);
    setShowResults(false);
    
    setTimeout(() => {
      setIsLoading(false);
      setShowResults(true);
      setTimeout(() => {
        resultsRef.current?.scrollIntoView({ behavior: 'smooth' });
      }, 100);
    }, 3000);
  };
  
  return (
    <div className="App">
      <Header />
      <Hero onAnalyzeClick={scrollToAnalyze} onSampleDemo={handleSampleDemo} />
      <AnalyzeSection 
        onAnalyze={handleAnalyze} 
        analyzeRef={analyzeRef}
        resumeFile={resumeFile}
        setResumeFile={setResumeFile}
        jobDescription={jobDescription}
        setJobDescription={setJobDescription}
      />
      
      {isLoading && <LoadingState />}
      
      {showResults && !isLoading && <ResultsSection resultsRef={resultsRef} />}
      
      <FeaturesSection />
      <TeamSection />zz
      <FAQSection />
      <Footer />
    </div>
  );
}

export default App;
