import { useState, useEffect, useRef } from 'react';
import './App.css';

// Custom Hook for count-up animation
function useCountUp(end, duration = 1500, shouldStart = true) {
  const [count, setCount] = useState(0);
  
  useEffect(() => {
    if (!shouldStart) return;
    
    let start = 0;
    const increment = end / (duration / 16);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        setCount(end);
        clearInterval(timer);
      } else {
        setCount(Math.floor(start));
      }
    }, 16);
    
    return () => clearInterval(timer);
  }, [end, duration, shouldStart]);
  
  return count;
}

// Header Component
function Header() {
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            CREDExA
            <span className="version">v1.2.0</span>
          </div>
          <div className="trust-badge">
            <span>ðŸ”’</span>
            <span>Built for transparency</span>
          </div>
        </div>
      </div>
    </header>
  );
}

// Hero Component
function Hero({ onAnalyzeClick }) {
  const statsCount1 = useCountUp(1247, 2000, true);
  const statsCount2 = useCountUp(12, 2000, true);
  
  return (
    <section className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="hero-eyebrow">Resume Analysis System</div>
          <h1 className="hero-headline">
            Understand why candidates matchâ€”or don't
          </h1>
          <p className="hero-subhead">
            Explainable scoring for better hiring decisions
          </p>
          <div className="hero-cta">
            <button className="btn btn-primary" onClick={onAnalyzeClick}>
              Analyze Resume â†’
            </button>
            <button className="btn btn-secondary">
              View Sample Report
            </button>
          </div>
          <div className="hero-stats">
            <div className="stat">
              <span className="stat-number">{statsCount1.toLocaleString()}</span>
              <span className="stat-label">Analyses completed</span>
            </div>
            <div className="stat">
              <span className="stat-number">{statsCount2} min</span>
              <span className="stat-label">Avg. time saved</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

// Analyze Section Component
function AnalyzeSection({ onAnalyze, analyzeRef }) {
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState('');
  const fileInputRef = useRef(null);
  
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setResumeFile(file);
    }
  };
  
  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file && file.type === 'application/pdf') {
      setResumeFile(file);
    }
  };
  
  const handleAnalyze = () => {
    if (resumeFile && jobDescription.trim()) {
      onAnalyze();
    }
  };
  
  return (
    <section className="analyze section" ref={analyzeRef}>
      <div className="container">
        <div className="text-center">
          <h2 className="section-title">Analyze Resume</h2>
          <p className="section-subtitle">
            Upload a resume and job description to see explainable match scoring
          </p>
        </div>
        
        <div 
          className="upload-area"
          onClick={() => fileInputRef.current?.click()}
          onDrop={handleDrop}
          onDragOver={(e) => e.preventDefault()}
        >
          <div className="upload-icon">ðŸ“„</div>
          <div className="upload-title">
            {resumeFile ? resumeFile.name : 'Upload Resume'}
          </div>
          <p className="upload-subtitle">
            Click to browse or drag and drop PDF files
          </p>
          <input 
            ref={fileInputRef}
            type="file" 
            accept=".pdf"
            className="upload-input"
            onChange={handleFileChange}
          />
        </div>
        
        <div className="upload-divider">or</div>
        
