// Main App Component
function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const analyzeRef = useRef(null);
  const resultsRef = useRef(null);
  
  const scrollToAnalyze = () => {
    analyzeRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
  
  const handleAnalyze = () => {
    setIsLoading(true);
    setShowResults(false);
    
    setTimeout(() => {
      setIsLoading(false);
      setShowResults(true);
      setTimeout(() => {
        resultsRef.current?.scrollIntoView({ behavior: 'smooth' });
      }, 100);
    }, 3000);
  };
  
  return (
    <div className="App">
      <Header />
      <Hero onAnalyzeClick={scrollToAnalyze} />
      <AnalyzeSection onAnalyze={handleAnalyze} analyzeRef={analyzeRef} />
      
      {isLoading && <LoadingState />}
      
      {showResults && !isLoading && <ResultsSection resultsRef={resultsRef} />}
      
      <FeaturesSection />
      <TeamSection />
      <FAQSection />
      <Footer />
    </div>
  );
}

export default App;
