import { useState, useEffect, useRef } from 'react';
import './App.css';
import Modal from './components/Modal';

// ------------------ HOOK ------------------
function useCountUp(end, duration = 1500, shouldStart = true) {
  const [count, setCount] = useState(0);

  useEffect(() => {
    if (!shouldStart) return;

    let start = 0;
    const increment = end / (duration / 16);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        setCount(end);
        clearInterval(timer);
      } else {
        setCount(Math.floor(start));
      }
    }, 16);

    return () => clearInterval(timer);
  }, [end, duration, shouldStart]);

  return count;
}

// ------------------ HEADER ------------------
function Header() {
  return (
    <header className="header">
      <div className="container header-content">
        <div className="logo">
          CREDExA <span className="version">v1.2.0</span>
        </div>
        <div className="trust-badge">ðŸ”’ Built for transparency</div>
      </div>
    </header>
  );
}

// ------------------ HERO ------------------
function Hero({ onAnalyzeClick }) {
  const stats1 = useCountUp(1247, 2000, true);
  const stats2 = useCountUp(12, 2000, true);

  return (
    <section className="hero">
      <div className="container hero-content">
        <div className="hero-eyebrow">Resume Analysis System</div>
        <h1>Understand why candidates matchâ€”or don't</h1>
        <p>Explainable scoring for better hiring decisions</p>

        <div className="hero-cta">
          <button className="btn btn-primary" onClick={onAnalyzeClick}>
            Analyze Resume â†’
          </button>
        </div>

        <div className="hero-stats">
          <div>
            <strong>{stats1}</strong>
            <span>Analyses completed</span>
          </div>
          <div>
            <strong>{stats2} min</strong>
            <span>Avg. time saved</span>
          </div>
        </div>
      </div>
    </section>
  );
}

// ------------------ ANALYZE SECTION ------------------
function AnalyzeSection({ onAnalyze, analyzeRef }) {
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState('');
  const fileInputRef = useRef(null);

  const canAnalyze = resumeFile && jobDescription.trim().length > 50;

  return (
    <section className="analyze section" ref={analyzeRef}>
      <div className="container">
        <h2 className="section-title text-center">Analyze Resume</h2>

        <div
          className="upload-area"
          onClick={() => fileInputRef.current.click()}
        >
          <div className="upload-icon">ðŸ“„</div>
          <div>{resumeFile ? resumeFile.name : 'Upload Resume (PDF)'}</div>
          <input
            ref={fileInputRef}
            type="file"
            accept=".pdf"
            hidden
            onChange={(e) => setResumeFile(e.target.files[0])}
          />
        </div>

        <textarea
          className="textarea"
          placeholder="Paste job description (min 50 characters)"
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />

        <button
          className="btn btn-primary"
          disabled={!canAnalyze}
          onClick={onAnalyze}
          style={{ opacity: canAnalyze ? 1 : 0.5 }}
        >
          Analyze Match â†’
        </button>
      </div>
    </section>
  );
}

// ------------------ LOADING ------------------
function LoadingState() {
  return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <p>Analyzing resume...</p>
    </div>
  );
}

// ------------------ RESULTS ------------------
function ResultsSection() {
  const score = 87;
  const scoreCount = useCountUp(score, 1500, true);

  return (
    <section className="results section">
      <h2 className="section-title text-center">Analysis Results</h2>

      <div className="card card-elevated text-center">
        <div className="score-number">{scoreCount}%</div>
        <div className="score-label">Overall Match Score</div>
        <span className="score-badge badge-strong">Strong Fit</span>
      </div>

      <div className="card">
        <h3>Why This Score?</h3>
        <ul>
          <li>âœ“ 8/10 required skills present (+12 pts)</li>
          <li>âœ“ 4 years experience (+8 pts)</li>
          <li>âš  Missing Docker, GraphQL (-7 pts)</li>
        </ul>
      </div>

      <div className="card">
        <h3>What Would Improve This Score?</h3>
        <ul>
          <li>Add Docker to skills (+5â€“7 pts)</li>
          <li>Highlight GraphQL projects (+3â€“5 pts)</li>
        </ul>
      </div>
    </section>
  );
}

// ------------------ FOOTER ------------------
function Footer() {
  return (
    <footer className="footer">
      Â© 2026 CREDExA â€” Built for transparency
    </footer>
  );
}

// ------------------ APP ------------------
function App() {
  const analyzeRef = useRef(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAnalyze = () => {
    setIsLoading(true);
    setIsModalOpen(false);

    setTimeout(() => {
      setIsLoading(false);
      setIsModalOpen(true);
    }, 3000);
  };

  return (
    <div className="App">
      <Header />
      <Hero onAnalyzeClick={() => analyzeRef.current.scrollIntoView({ behavior: 'smooth' })} />
      <AnalyzeSection onAnalyze={handleAnalyze} analyzeRef={analyzeRef} />

      {isLoading && <LoadingState />}

      <Modal
        isOpen={isModalOpen && !isLoading}
        onClose={() => setIsModalOpen(false)}
      >
        <ResultsSection />
      </Modal>

      <Footer />
    </div>
  );
}

export default App;
