import { analyzeResume } from "./services/api";
import { useState, useEffect, useRef } from "react";
import "./App.css";

// Custom Hook for count-up animation
function useCountUp(end, duration = 1500, shouldStart = true) {
  const [count, setCount] = useState(0);

  useEffect(() => {
    if (!shouldStart) return;

    let start = 0;
    const increment = end / (duration / 16);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        setCount(end);
        clearInterval(timer);
      } else {
        setCount(Math.floor(start));
      }
    }, 16);

    return () => clearInterval(timer);
  }, [end, duration, shouldStart]);

  return count;
}

/* ================= HEADER ================= */

function Header() {
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            CREDExA <span className="version">v1.2.0</span>
          </div>
          <div className="trust-badge">
            <span>ðŸ”’</span>
            <span>Built for transparency</span>
          </div>
        </div>
      </div>
    </header>
  );
}

/* ================= HERO ================= */

function Hero({ onAnalyzeClick }) {
  const stats1 = useCountUp(1247, 2000, true);
  const stats2 = useCountUp(12, 2000, true);

  return (
    <section className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="hero-eyebrow">Resume Analysis System</div>
          <h1 className="hero-headline">
            Understand why candidates matchâ€”or don't
          </h1>
          <p className="hero-subhead">
            Explainable scoring for better hiring decisions
          </p>
          <div className="hero-cta">
            <button className="btn btn-primary" onClick={onAnalyzeClick}>
              Analyze Resume â†’
            </button>
            <button className="btn btn-secondary">View Sample Report</button>
          </div>
          <div className="hero-stats">
            <div className="stat">
              <span className="stat-number">{stats1}</span>
              <span className="stat-label">Analyses completed</span>
            </div>
            <div className="stat">
              <span className="stat-number">{stats2} min</span>
              <span className="stat-label">Avg. time saved</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

/* ================= ANALYZE ================= */

function AnalyzeSection({ onAnalyze, analyzeRef }) {
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const fileInputRef = useRef(null);

  return (
    <section className="analyze section" ref={analyzeRef}>
      <div className="container">
        <div className="text-center">
          <h2 className="section-title">Analyze Resume</h2>
          <p className="section-subtitle">
            Upload a resume and job description to see explainable match scoring
          </p>
        </div>

        <div
          className="upload-area"
          onClick={() => fileInputRef.current.click()}
        >
          <div className="upload-icon">ðŸ“„</div>
          <div className="upload-title">
            {resumeFile ? resumeFile.name : "Upload Resume"}
          </div>
          <input
            ref={fileInputRef}
            type="file"
            accept=".pdf"
            className="upload-input"
            onChange={(e) => setResumeFile(e.target.files[0])}
          />
        </div>

        <div className="upload-divider">or</div>

        <textarea
          className="textarea"
          placeholder="Paste job description here..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />

        <div className="analyze-btn-container">
          <button
            className="btn btn-primary"
            onClick={() => onAnalyze(resumeFile, jobDescription)}
            disabled={!resumeFile || !jobDescription.trim()}
          >
            Analyze Match â†’
          </button>
        </div>
      </div>
    </section>
  );
}

/* ================= LOADING ================= */

function LoadingState() {
  return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <div className="loading-text">Analyzing resume...</div>
    </div>
  );
}

/* ================= RESULTS ================= */

function ResultsSection({ resultsRef, results }) {
  const score = results?.overall_score || 0;
  const scoreCount = useCountUp(score, 1200, true);

  return (
    <section className="results section" ref={resultsRef}>
      <div className="container">
        <h2 className="section-title text-center">Analysis Results</h2>

        <div className="card card-elevated">
          <div className="score-display">
            <div className="score-number">{scoreCount}%</div>
            <div className="score-label">Match Score</div>
          </div>
        </div>

        {results?.review && (
          <div className="card">
            <h3>AI Review</h3>
            <p>{results.review.overall_review}</p>

            <h4>Strengths</h4>
            <ul>
              {results.review.strengths.map((s, i) => (
                <li key={i}>{s}</li>
              ))}
            </ul>

            <h4>Areas to Improve</h4>
            <ul>
              {results.review.areas_to_improve.map((a, i) => (
                <li key={i}>{a}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </section>
  );
}

/* ================= MAIN APP ================= */

function App() {
  const analyzeRef = useRef(null);
  const resultsRef = useRef(null);

  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);

  const scrollToAnalyze = () =>
    analyzeRef.current.scrollIntoView({ behavior: "smooth" });

  // âœ… ONLY FIXED FUNCTION (BACKEND CONNECTED)
  const handleAnalyze = async (resumeFile, jobDescription) => {
    setLoading(true);
    try {
      const data = await analyzeResume(resumeFile, jobDescription);
      setResults(data);
      setTimeout(() => {
        resultsRef.current.scrollIntoView({ behavior: "smooth" });
      }, 200);
    } catch (err) {
      alert("Analysis failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <Header />
      <Hero onAnalyzeClick={scrollToAnalyze} />
      <AnalyzeSection onAnalyze={handleAnalyze} analyzeRef={analyzeRef} />
      {loading && <LoadingState />}
      {results && !loading && (
        <ResultsSection resultsRef={resultsRef} results={results} />
      )}
    </div>
  );
}

export default App;
