// Loading Component
function LoadingState() {
  const [activeStep, setActiveStep] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setActiveStep((prev) => (prev < 2 ? prev + 1 : prev));
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <div className="loading-text">Analyzing resume...</div>
      <div className="loading-steps">
        <div className={`loading-step ${activeStep >= 0 ? "completed" : ""}`}>
          <span className="step-icon">{activeStep >= 0 ? "âœ“" : "â—‹"}</span>
          <span>Parsing document</span>
        </div>
        <div
          className={`loading-step ${activeStep === 1 ? "active" : activeStep > 1 ? "completed" : ""}`}
        >
          <span className="step-icon">
            {activeStep > 1 ? "âœ“" : activeStep === 1 ? "âŸ³" : "â—‹"}
