import { analyzeResume } from "./services/api";
import { useState, useEffect, useRef } from "react";
import "./App.css";

/* ---------- everything ABOVE remains UNCHANGED ---------- */
/* ---------- all components: Header, Hero, AnalyzeSection, 
   LoadingState, ResultsSection, FeaturesSection, TeamSection,
   FAQSection, Footer stay EXACTLY the same ---------- */

/* ================= MAIN APP (ONLY FIXED PART) ================= */

function App() {
  const analyzeRef = useRef(null);
  const resultsRef = useRef(null);

  const [isLoading, setIsLoading] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState(null);

  const scrollToAnalyze = () => {
    analyzeRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // âœ… THIS IS THE ONLY IMPORTANT FIX
  const handleAnalyze = async (resumeFile, jobDescription) => {
    setIsLoading(true);
    setShowResults(false);

    try {
      const data = await analyzeResume(resumeFile, jobDescription);
      setResults(data);
      setShowResults(true);

      setTimeout(() => {
        resultsRef.current?.scrollIntoView({ behavior: "smooth" });
      }, 150);
    } catch (err) {
      console.error(err);
      alert("Analysis failed. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <Header />
      <Hero onAnalyzeClick={scrollToAnalyze} />

      <AnalyzeSection
        onAnalyze={handleAnalyze}
        analyzeRef={analyzeRef}
      />

      {isLoading && <LoadingState />}

      {showResults && !isLoading && (
        <ResultsSection
          resultsRef={resultsRef}
          results={results}
        />
      )}

      {/* ðŸ”’ NOTHING BELOW THIS IS TOUCHED */}
      <FeaturesSection />
      <TeamSection />
      <FAQSection />
      <Footer />
    </div>
  );
}

export default App;
