import { analyzeResume } from "./services/api";
import { useState, useEffect, useRef } from "react";
import "./App.css";

/* =======================
   Custom Hook
======================= */
function useCountUp(end, duration = 1500, shouldStart = true) {
  const [count, setCount] = useState(0);

  useEffect(() => {
    if (!shouldStart) return;

    let start = 0;
    const increment = end / (duration / 16);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        setCount(end);
        clearInterval(timer);
      } else {
        setCount(Math.floor(start));
      }
    }, 16);

    return () => clearInterval(timer);
  }, [end, duration, shouldStart]);

  return count;
}

/* =======================
   Header
======================= */
function Header() {
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            CREDExA <span className="version">v1.2.0</span>
          </div>
          <div className="trust-badge">
            <span>ðŸ”’</span>
            <span>Built for transparency</span>
          </div>
        </div>
      </div>
    </header>
  );
}

/* =======================
   Hero
======================= */
function Hero({ onAnalyzeClick }) {
  const statsCount1 = useCountUp(1247, 2000, true);
  const statsCount2 = useCountUp(12, 2000, true);

  return (
    <section className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="hero-eyebrow">Resume Analysis System</div>
          <h1 className="hero-headline">
            Understand why candidates matchâ€”or don't
          </h1>
          <p className="hero-subhead">
            Explainable scoring for better hiring decisions
          </p>
          <div className="hero-cta">
            <button className="btn btn-primary" onClick={onAnalyzeClick}>
              Analyze Resume â†’
            </button>
          </div>
          <div className="hero-stats">
            <div className="stat">
              <span className="stat-number">
                {statsCount1.toLocaleString()}
              </span>
              <span className="stat-label">Analyses completed</span>
            </div>
            <div className="stat">
              <span className="stat-number">{statsCount2} min</span>
              <span className="stat-label">Avg. time saved</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

/* =======================
   Analyze Section
======================= */
function AnalyzeSection({ onAnalyze, analyzeRef }) {
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const fileInputRef = useRef(null);

  const handleAnalyzeClick = () => {
    if (resumeFile && jobDescription.trim()) {
      onAnalyze(resumeFile, jobDescription);
    }
  };

  return (
    <section className="analyze section" ref={analyzeRef}>
      <div className="container">
        <div className="upload-area" onClick={() => fileInputRef.current.click()}>
          <div className="upload-title">
            {resumeFile ? resumeFile.name : "Upload Resume"}
          </div>
          <input
            ref={fileInputRef}
            type="file"
            accept=".pdf"
            className="upload-input"
            onChange={(e) => setResumeFile(e.target.files[0])}
          />
        </div>

        <textarea
          className="textarea"
          placeholder="Paste job description here..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
        />

        <button
          className="btn btn-primary"
          onClick={handleAnalyzeClick}
          disabled={!resumeFile || !jobDescription}
        >
          Analyze Match â†’
        </button>
      </div>
    </section>
  );
}

/* =======================
   Results Section
======================= */
function ResultsSection({ resultsRef, results }) {
  const scoreCount = useCountUp(results.overall_score, 1500, true);

  const badgeClass =
    results.score_band === "Strong Match"
      ? "badge-strong"
      : results.score_band === "Moderate Match"
      ? "badge-moderate"
      : "badge-weak";

  return (
    <section className="results section" ref={resultsRef}>
      <div className="container">
        <h2>Overall Match</h2>
        <h1>{scoreCount}%</h1>
        <span className={`score-badge ${badgeClass}`}>
          {results.score_band}
        </span>

        <p>{results.score_explanation}</p>

        <h3>Role</h3>
        <p>{results.role}</p>

        <h3>AI Review</h3>
        <p>{results.review.overall_review}</p>

        <h4>Strengths</h4>
        <ul>
          {results.review.strengths.map((s, i) => (
            <li key={i}>{s}</li>
          ))}
        </ul>

        <h4>Areas to Improve</h4>
        <ul>
          {results.review.areas_to_improve.map((a, i) => (
            <li key={i}>{a}</li>
          ))}
        </ul>
      </div>
    </section>
  );
}

/* =======================
   App
======================= */
export default function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [results, setResults] = useState(null);
  const analyzeRef = useRef(null);
  const resultsRef = useRef(null);

  const handleAnalyze = async (resumeFile, jobDescription) => {
    try {
      setIsLoading(true);
      setResults(null);

      const data = await analyzeResume(resumeFile, jobDescription);
      setResults(data);

      setTimeout(() => {
        resultsRef.current?.scrollIntoView({ behavior: "smooth" });
      }, 200);
    } catch (err) {
      alert("Failed to analyze resume");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <Header />
      <Hero onAnalyzeClick={() => analyzeRef.current.scrollIntoView()} />
      <AnalyzeSection onAnalyze={handleAnalyze} analyzeRef={analyzeRef} />

      {isLoading && <p>Analyzing...</p>}

      {results && !isLoading && (
        <ResultsSection resultsRef={resultsRef} results={results} />
      )}
    </div>
  );
}
