import { analyzeResume } from "./api";
import { useState, useEffect, useRef } from "react";
import "./App.css";

// Custom Hook for count-up animation
function useCountUp(end, duration = 1500, shouldStart = true) {
  const [count, setCount] = useState(0);

  useEffect(() => {
    if (!shouldStart) return;

    let start = 0;
    const increment = end / (duration / 16);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        setCount(end);
        clearInterval(timer);
      } else {
        setCount(Math.floor(start));
      }
    }, 16);

    return () => clearInterval(timer);
  }, [end, duration, shouldStart]);

  return count;
}

// Header Component
function Header() {
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            CREDExA
            <span className="version">v1.2.0</span>
          </div>
          <div className="trust-badge">
            <span>ðŸ”’</span>
            <span>Built for transparency</span>
          </div>
        </div>
      </div>
    </header>
  );
}

// Hero Component
function Hero({ onAnalyzeClick }) {
  const statsCount1 = useCountUp(1247, 2000, true);
  const statsCount2 = useCountUp(12, 2000, true);

  return (
    <section className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="hero-eyebrow">Resume Analysis System</div>
          <h1 className="hero-headline">
            Understand why candidates matchâ€”or don't
          </h1>
